renderHTML:
__ht__function(data, gv, cache) {var option = {
    color: ['rgb(51,153,255)', '#FEB64D', 'rgb(93,217,174)', 'rgb(204,104,166)'],
    tooltip: {
        trigger: 'axis',
        axisPointer: {
            type: 'cross',
            label: {
                backgroundColor: '#6a7985'
            }
        }
    },
    legend: {
        textStyle: {
            color: 'rgb(153, 153, 153)'
        },
        bottom: '2%'
    },
    grid: {
        top: '3%',
        left: '3%',
        right: '10%',
        bottom: '15%',
        containLabel: true
    },
    xAxis: {
        type: 'category',
        boundaryGap: false,
        axisLine: {
            show: false
        },
        axisLabel: {
            color: 'rgb(153, 153, 153)'
        },
        axisTick: {
            show: false
        },
        splitLine: {
            show: false,
            lineStyle: {
                color: 'rgb(153, 153, 153)'
            }
        }
    },
    yAxis: {
        type: 'value',
         axisLine: {
            show: false
        },
        axisLabel: {
            color: 'rgb(153, 153, 153)'
        },
        axisTick: {
            show: false
        },
        splitLine: {
            show: false,
            lineStyle: {
                color: 'rgb(153, 153, 153)'
            }
        }
    }
};

if (!cache.htmlView) {
    cache.htmlView = document.createElement('div');
    cache.htmlView.style.position = 'absolute';
    cache.htmlChart = echarts.init(cache.htmlView);

    // layoutHTML will be called when the data needs to be drawing
    cache.htmlView.layoutHTML = function() {
        gv.layoutHTML(data, cache.htmlView, true);
        cache.htmlChart.resize();
    };
}

// update data
option.xAxis.data = data.a('xAxis');
option.legend.data = data.a('legendData');
option.series = data.a('series');

cache.htmlChart.setOption(option);

// html for showing
return cache.htmlView;}
